import { useState } from 'react';
import styles from './App.module.css';
import poweredImage from './assets/powered.png';
import leftArrowImage from './assets/leftarrow.png';
import { GridItem } from './components/gridItem';
import { levels, calculateImc, Level } from './helpers/imc';

const App = () => {
    const [heightField, setHeightField] = useState('');
    const [weightField, setWeightField] = useState('');
    const [toShow, setToShow] = useState<Level | null>(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [history, setHistory] = useState<{ height: number; weight: number; imc: number; classification: string }[]>([]);

    const handleCalculateButton = () => {
        if (heightField && weightField) {
            const height = parseFloat(heightField);
            const weight = parseFloat(weightField);
    
            if (height > 0 && weight > 0) {
                const result = calculateImc(height, weight);
                setToShow(result);
                setHistory(prevHistory => [
                    ...prevHistory,
                    { height, weight, imc: result.imc, classification: result.classification }
                ]);
                setErrorMessage('');
                setSuccessMessage('Cálculo realizado com sucesso!');
            } else {
                setErrorMessage('Por favor, insira valores válidos para altura e peso.');
                setSuccessMessage('');
            }
        } else {
            setErrorMessage('Digite todos os campos!');
            setSuccessMessage('');
        }
    };

    const handleBackButton = () => {
        setToShow(null);
        setSuccessMessage('');
    };

    return (
        <div className={styles.main}>
            <header>
                <div className={styles.headerContainer}>
                    <img src={poweredImage} alt="Logo" width={100} />
                </div>
            </header>
            <div className={styles.container}>
                <div className={styles.leftSide}>
                    <h1>Calcule o seu IMC.</h1>
                    {errorMessage && <div className={styles.errorMessage}>{errorMessage}</div>}
                    {successMessage && <div className={styles.successMessage}>{successMessage}</div>}
                    <input
                        type="number"
                        placeholder="Digite a sua altura. Ex: 180 (em cm)"
                        value={heightField}
                        onChange={e => setHeightField(e.target.value)}
                        disabled={!!toShow}
                    />
                    <input
                        type="number"
                        placeholder="Digite o seu peso. Ex: 80 (em kg)"
                        value={weightField}
                        onChange={e => setWeightField(e.target.value)}
                        disabled={!!toShow}
                    />
                    <button onClick={handleCalculateButton} disabled={!!toShow}>Calcular</button>
                </div>
                <div className={styles.rightSide}>
                    {!toShow &&
                        <div className={styles.grid}>
                            {levels.map((item, key) => (
                                <GridItem key={key} item={item} />
                            ))}
                        </div>
                    }
                    {toShow &&
                        <div className={styles.rightBig}>
                            <div className={styles.rightArrow} onClick={handleBackButton}>
                                <img src={leftArrowImage} alt="" width={25} />
                            </div>
                            <GridItem item={toShow} />
                        </div>
                    }
                    <div className={styles.history}>
                        <h2>Histórico de Cálculos</h2>
                        {history.map((item, index) => (
                            <div key={index} className={styles.historyItem}>
                                Altura: {item.height} cm, Peso: {item.weight} kg, IMC: {item.imc.toFixed(2)}, Classificação: {item.classification}
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default App;
