export type Level = {
    title: string
    color: string
    icon: 'down' | 'up'
    imc: number[]
    yourImc?: number
}

export const levels: Level[] = [
    { title: 'Abaixo do Peso', color: '#96A3AB', icon: 'down', imc: [0, 18.55] },
    { title: 'Normal', color: '#0EAD69', icon: 'up', imc: [18.56, 24.99], level: "Parabéns! Continue com uma rotina de exercícios variada, incluindo cardio e treinamento de força." },
    { title: 'Sobrepeso', color: '#E2B039', icon: 'down', imc: [25, 30.09], level: "É importante focar em exercícios aeróbicos para queimar calorias, combinados com treinos de resistência para fortalecer os músculos." },
    { title: 'Obesidade', color: '#C3423F', icon: 'down', imc: [30.1, 99], level: "Recomendamos procurar a orientação de um profissional de saúde para um plano de exercícios seguro e eficaz, adaptado às suas necessidades." }
]

export const calculateImc = (height: number, weight: number) => {
    const imc = weight / (height * height)

    for (let i in levels) {
        if (imc >= levels[i].imc[0] && imc < levels[i].imc[1]) {
            let levelCopy: Level = {...levels[i]}

            levelCopy.yourImc = imc
            return levelCopy
        }
    }

    return null
}
