export const getExerciseRecommendation = (imcLevel: string) => {
    switch (imcLevel) {
        case 'Abaixo do peso':
            return "Recomendamos focar em exercícios de fortalecimento muscular e aumento de massa magra. Consulte um profissional de saúde para um plano personalizado.";
        case 'Peso normal':
            return "Parabéns! Continue com uma rotina de exercícios variada, incluindo cardio e treinamento de força.";
        case 'Sobrepeso':
            return "É importante focar em exercícios aeróbicos para queimar calorias, combinados com treinos de resistência para fortalecer os músculos.";
        case 'Obesidade':
            return "Recomendamos procurar a orientação de um profissional de saúde para um plano de exercícios seguro e eficaz, adaptado às suas necessidades.";
        default:
            return "Não foram encontradas recomendações de exercícios para este nível de IMC.";
    }
};
