import React, { useState } from 'react';
import styles from './App.module.css';
import poweredImage from './assets/powered.png';
import leftArrowImage from './assets/leftarrow.png';
import { GridItem } from './components/gridItem';
import { levels, calculateImc, Level } from './helpers/imc';

const App = () => {
    const [heightField, setHeightField] = useState('');
    const [weightField, setWeightField] = useState('');
    const [birthdate, setBirthdate] = useState('');
    const [toShow, setToShow] = useState<Level | null>(null);
    const [errorMessage, setErrorMessage] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleCalculateImcButton = () => {
        if (heightField && weightField) {
            const height = parseFloat(heightField);
            const weight = parseFloat(weightField);

            if (height > 0 && weight > 0) {
                const result = calculateImc(height, weight);
                setToShow(result);
                setErrorMessage('');
                setSuccessMessage('Cálculo de IMC realizado com sucesso!');
            } else {
                setErrorMessage('Por favor, insira valores válidos para altura e peso.');
                setSuccessMessage('');
            }
        } else {
            setErrorMessage('Digite todos os campos!');
            setSuccessMessage('');
        }
    };

    const calculateAge = () => {
        if (birthdate) {
            const today = new Date();
            const birthDate = new Date(birthdate);
            const age = today.getFullYear() - birthDate.getFullYear();
            const month = today.getMonth() - birthDate.getMonth();
            if (month < 0 || (month === 0 && today.getDate() < birthDate.getDate())) {
                return age - 1;
            }
            return age;
        }
        return '';
    };

    const handleCalculateAgeButton = () => {
        const age = calculateAge();
        if (age !== '') {
            setSuccessMessage(`Idade calculada com sucesso: ${age} anos.`);
            setErrorMessage('');
        } else {
            setErrorMessage('Digite uma data de nascimento válida.');
            setSuccessMessage('');
        }
    };

    const handleBackButton = () => {
        setToShow(null);
        setSuccessMessage('');
        setErrorMessage('');
    };

    return (
        <div className={styles.main}>
            <div className={styles.container}>
                <div className={styles.leftSide}>
                    <h1>Calculadora</h1>
                    {errorMessage && <div className={styles.errorMessage}>{errorMessage}</div>}
                    {successMessage && <div className={styles.successMessage}>{successMessage}</div>}
                    <input
                        type="number"
                        placeholder="Altura (m)"
                        value={heightField}
                        onChange={e => setHeightField(e.target.value)}
                        disabled={!!toShow}
                    />
                    <input
                        type="number"
                        placeholder="Peso (kg)"
                        value={weightField}
                        onChange={e => setWeightField(e.target.value)}
                        disabled={!!toShow}
                    />
                    <button onClick={handleCalculateImcButton} disabled={!!toShow}>Calcular IMC</button>
                    <input
                        type="date"
                        placeholder="Data de Nascimento"
                        value={birthdate}
                        onChange={e => setBirthdate(e.target.value)}
                        disabled={!!toShow}
                    />
                    <button onClick={handleCalculateAgeButton}>Calcular Idade</button>
                </div>
                <div className={styles.rightSide}>
                    {!toShow &&
                        <div className={styles.grid}>
                            {levels.map((item, key) => (
                                <GridItem key={key} item={item} />
                            ))}
                        </div>
                    }
                    {toShow &&
                        <div className={styles.rightBig}>
                            <div className={styles.rightArrow} onClick={handleBackButton}>
                                <img src={leftArrowImage} alt="" width={25} />
                            </div>
                            <GridItem item={toShow} />
                        </div>
                    }
                </div>
            </div>
        </div>
    );
};

export default App;
